const findExportSymbol = (
  checker: ts.TypeChecker,
  moduleSymbol: ts.Symbol,
  exportName: string
): ts.Symbol | undefined =>
  checker
    .getExportsOfModule(moduleSymbol)
    .find((symbol) => symbol.getName() === exportName)

